    public void CreditCardChecker() {
        int[] d = new int [16];
        String s;
        s = jTextField1.getText();
        
        for (int i = 0; i < 16; i++) {
            d[i] = 0;
        }
        
        for (int i = 0; i < 16; i++) {
            d[i] = Integer.parseInt(String.valueOf(s.charAt(i)));
        }
        
        String text1 = "test";
        int j = 0;
        int total = 0;

        for (int i = 0; i < 16; i++) {
            if (i % 2 == 1) {
                total += d[i];
            } else {
                j = d[i] * 2;
                if (j >= 10) {
                    j = j -9;
                }
                total += j;
            }
        }
        String digits = "";
        
        if (total % 10 == 0) {
            text1 = "Verified";
        } else {
            text1 = "Failed";
        }
        
        for (int i = 0; i < 16; i++) {
            digits += d[i];
        }

        jTextArea1.setText(text1 + " " + digits);

        System.out.println("Break");
        
    }
    
    
