    public boolean verifyISBNcode(int[] code) {
        int calc = 1;
        for (int i = 0; i < 10; i++) {
            calc *= code[i]+ 2;
            System.out.println(calc);
        }
        calc = calc % 11;

//        System.out.println(calc);

        if (calc == 0) {
            return true;
        } else {
            return false;
        }
    }
    
